mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO board (
board_title
) VALUES (
#{boardTitle}
)

mapper.select:
SELECT board_no AS boardNo,
	   board_title AS boardTitle
 FROM board WHERE board_no = #{boardNo}

mapper.update:
UPDATE board
SET board_title = #{boardTitle}
WHERE board_no = #{boardNo}
